import { useRef, useEffect, useState } from 'react';
import axios from 'axios';
import './login.css';

const Login = ({login})=>{

    const [l, setLogin] = useState(false);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const loginProcess = ()=> setLogin(!l);

    useEffect(()=>{
        const fetchData = async()=>{
            const body = JSON.stringify({
                e : `${email}`,
                p : `${password}`
            })
            await axios.post("/login", body);
        } 
        fetchData();
        console.log(email);
        console.log(password);
    },[l])

    if(login){
        return(
            <div className="login px-5">
                <input type="text" className="my-2 py-2" placeholder="Email" id="email"
                    onChange={(e)=>{setEmail(e.target.value)}}
                />
                <input type="password" placeholder="Password" id="password" 
                    onChange={(e)=>{setPassword(e.target.value)}}
                />
                <button className="my-2 btn btn-outline-dark btn-bg" onClick={loginProcess}>Login</button>
                <div>
                    <span>New to us - </span>
                    <a href="">Create an Account</a>
                </div>
            </div>
        );
    }
    return(
        <></>
    );
}

export default Login;
