import axios from 'axios';
import React, { useEffect, useState, useRef } from 'react';
import './engine.css';
import Create from '../Create/create';

const Engine = (prop)=>{

    const [searchText, setSearch] = useState('');
    const [result, setResult] = useState([]);
    const deleteSearch = useRef();
    const resDiv = useRef();

    const DeleteSearch = ()=>{
        setSearch('');
    }

    useEffect(()=>{
        if(searchText.length > 0){
            axios.get(`/searchNote?q=${searchText}`)
            .then((ob)=>{
                console.log(ob.data);
                setResult(ob.data.d);
            })
            .catch((ob)=>{
                console.log(ob);
            })
        }
    },[searchText])

    if(prop.search){
        return(
            <>
                <div className="engine container-fluid p-3">
                    <input type="text"  className="p-1" placeholder="Search your notes"
                        onChange={(e)=>{
                            setSearch(e.target.value);
                        }}
                        value={searchText}
                    />
                    <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" class="bi bi-x-circle-fill" viewBox="0 0 16 16" ref={deleteSearch} onClick={DeleteSearch}>
                    <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM5.354 4.646a.5.5 0 1 0-.708.708L7.293 8l-2.647 2.646a.5.5 0 0 0 .708.708L8 8.707l2.646 2.647a.5.5 0 0 0 .708-.708L8.707 8l2.647-2.646a.5.5 0 0 0-.708-.708L8 7.293 5.354 4.646z"/>
                    </svg>
                </div>
                <div className="result container-fluid row" ref={resDiv}>
                    <div className="list col">
                        <ul>
                            {
                                result.map((res)=>(
                                    <li className='row'>
                                        {res.title}
                                        <span className='col'>
                                            Edit
                                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-arrow-right-circle-fill" viewBox="0 0 16 16">
                                            <path d="M8 0a8 8 0 1 1 0 16A8 8 0 0 1 8 0zM4.5 7.5a.5.5 0 0 0 0 1h5.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3a.5.5 0 0 0 0-.708l-3-3a.5.5 0 1 0-.708.708L10.293 7.5H4.5z"/>
                                            </svg>
                                        </span>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    <div className="edit col">
                        <Create create={true}/>
                    </div>
                </div>
            </>
        );
    }
}

export default Engine;
