import { useState,useEffect, useRef } from 'react';
import './App.css';
import Nav from './components/navBar/nav';
import Home from './components/Home/home';
import Home2 from './components/Home/home2';
import Create from './components/Create/create';
import Login from './components/Login/login';
import Register from './components/Register/register';


function App() {

  const [show, setShow] = useState({
    home : true,
    create : false,
    search : false,
    login : false,
    register : false
  });

  useEffect(()=>{
      const h = document.querySelector(".nav-link:nth-of-type(1)");
      if(show.home) h.style.borderBottom = "1px solid black";
      else h.style.borderBottom = "1px solid transparent";
  })

  const RenderHome = ()=>{
    setShow(
            {
              home : true,
              create : false,
              search : false,
              login : false,
              register : false
            }
          )
  }
  const RenderCreate = ()=>{
    setShow(
            {
              home : false,
              create : true,
              search : false,
              login : false,
              register : false
            }
          )
  }
  const RenderSearch = ()=>{
    setShow(
            {
              home : false,
              create : false,
              search : true,
              login : false,
              register : false
            }
          )
  }
  const RenderLogin = ()=>{
    setShow(
            {
              home : false,
              create : false,
              search : false,
              login : true,
              register : false
            }
          )
  }



  return(
    <>
        <div className="bg"></div>
        <nav className="navbar navbar-expand-lg">
            <div className="container-fluid p-5">
                <a className="navbar-brand mx-5" href="#">
                <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" fill="currentColor" class="bi bi-text-paragraph" viewBox="0 0 16 16">
                    <path fill-rule="evenodd" d="M2 12.5a.5.5 0 0 1 .5-.5h7a.5.5 0 0 1 0 1h-7a.5.5 0 0 1-.5-.5zm0-3a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5zm0-3a.5.5 0 0 1 .5-.5h11a.5.5 0 0 1 0 1h-11a.5.5 0 0 1-.5-.5zm4-3a.5.5 0 0 1 .5-.5h7a.5.5 0 0 1 0 1h-7a.5.5 0 0 1-.5-.5z"/>
                </svg>
                </a>
                <div className="navbar-nav">
                <a className="nav-link mx-5 fw-normal fs-6" onClick={RenderHome}>Home</a>
                    <a className="nav-link mx-5 fw-normal fs-6" onClick={RenderCreate}>Create</a>
                    <a className="nav-link mx-5 fw-normal fs-6" onClick={RenderSearch}>Search</a>
                    <a className="nav-link mx-5 fw-normal fs-6" onClick={RenderLogin}>Login</a>
                </div>
            </div>
        </nav>
        <div className="home container-fluid p-5 row m-0">
              <h2 className="col p-5 mx-4 my-1 fw-bold fs-2" value={`Hello`}></h2>
              <div className="col container-fluid">
                <Home2 home={show.home} />
                <Create create={show.create}/>
                <Login login={show.login}/>
              </div>
          </div>
      </>
  );


}

export default App;
