import { useEffect, useRef, useState } from 'react';
import './create.css';

const Create = ({create})=>{

    useEffect(()=>{
        const c = document.querySelector(".nav-link:nth-of-type(2)");
        if(create) c.style.borderBottom = "1px solid black";
        else c.style.borderBottom = "1px solid transparent";
    })

    const [heart, setHeart] = useState('bi bi-heart col-3');

    const like = ()=>{
        setHeart('bi bi-heart-fill col-3');
    }

    if(create){
        return(
            <div className="create px-5">
                <input type="text" className="my-2 py-2" placeholder="Title"/>
                <textarea name="note" cols="55" rows="10" className="" placeholder="Content"></textarea>
                <div className='container-fluid row-10'>
                    <button className="my-2 btn btn-outline-dark btn-bg col-7">Save</button>
                    <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="red" className={heart} viewBox="0 0 16 16" onClick={like}>
                    <path d="m8 2.748-.717-.737C5.6.281 2.514.878 1.4 3.053c-.523 1.023-.641 2.5.314 4.385.92 1.815 2.834 3.989 6.286 6.357 3.452-2.368 5.365-4.542 6.286-6.357.955-1.886.838-3.362.314-4.385C13.486.878 10.4.28 8.717 2.01L8 2.748zM8 15C-7.333 4.868 3.279-3.04 7.824 1.143c.06.055.119.112.176.171a3.12 3.12 0 0 1 .176-.17C12.72-3.042 23.333 4.867 8 15z"/>
                    </svg>
                </div>
            </div>
        );
    }
    return(
        <></>
    );
}

export default Create;
