import './home.css';
import Home2 from './home2';
import Create from '../Create/create';
import Login from '../Login/login';


const Home = ({s})=>{
    console.log(s);
    console.log(s.home)
    useEffect(()=>{
        const h = document.querySelector(".nav-link:nth-of-type(1)");
        if(s.home) h.style.borderBottom = "1px solid black";
        else h.style.borderBottom = "1px solid transparent";
    })
    return(
        <div className="home container-fluid p-5 row m-0">
            <h2 className="col p-5 mx-4 my-1 fw-bold fs-2">
                Your personal notes storage app !!
            </h2>
        </div>
    );
}


export default Home;
