import { useEffect } from 'react';
import './create.css';

const Create = ({value})=>{

    const c = document.querySelector(".nav-link:nth-of-type(2)");
    if(value) c.style.borderBottom = "1px solid black";
    else c.style.borderBottom = "1px solid transparent";


    if(value){
        return(
            <div className="create px-5">
                <input type="text" className="my-2 py-2" placeholder="Title"/>
                <textarea name="note" cols="50" rows="10" className="" placeholder="Content"></textarea>
                <button className="my-2 btn btn-outline-dark btn-bg">Save</button>
            </div>
        );
    }
    return(
        <></>
    );
}

export default Create;
