import { useRef, useEffect, useState } from 'react';
import axios from 'axios';
import './login.css';

const Login = (prop)=>{

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [error, setError]  = useState("");
    const ERROR = useRef();

    const loginProcess = ()=>{
        const fetchData = async()=>{
            await axios({
                method: "post",
                url: "/login",
                headers: {},
                data: {
                    e : `${email}`,
                    p : `${password}`
                }
            });
        }
        if(email != "" && password != "") fetchData();
        else{
            setError("Do fill every field");
            console.log(ERROR);
            // ERROR.current.style.display = "block";
            // setInterval(()=>{
            //     ERROR.current.style.display = "none";
            // }, 2000);
        }
    }

    if(prop.login){
        return(
            <div className="login px-5">
                <input type="text" className="my-2 py-2" placeholder="Email" id="email"
                    onChange={(e)=>{setEmail(e.target.value)}}
                />
                <input type="password" className="py-2" placeholder="Password" id="password" 
                    onChange={(e)=>{setPassword(e.target.value)}}
                />
                <button className="my-2 btn btn-outline-dark btn-bg" onClick={loginProcess}>Login</button>
                <p style={{display : "block", fontSize : "0.5em", color: "red"}} ref={ERROR}>{error}</p>
                <div className="createAccount">
                    <span>New to us - </span>
                    <a onClick={prop.regAlert}>Create an Account</a>
                </div>
            </div>
        );
    }
    return(
        <></>
    );
}

export default Login;
