import axios from "axios";
import { useEffect, useRef, useState } from "react";
import './register.css';

const Register = ({Reg})=>{

    const [register, setReg] = useState(false);
    const [email, setEmail] = useState();
    const [username, setUser] = useState();
    const [password, setPassword] = useState();
    const [conpassword, setConPassword] = useState();
    const [err, setErr] = useState('');
    const loginProcess = ()=> setReg(!register);

    useEffect(()=>{
        const reg = async ()=>{
            await axios({
                method : "post",
                header : {},
                url : "/register",
                data : {
                    user : `${username}`,
                    e : `${email}`,
                    p : `${password}`
                }
            })
        }
        if(password == conpassword) reg();
        else setErr('Check Here !!');
    }, [register])

    if(Reg){
        return(
            <div className="register px-2">
                <input type="text" className="p-1" placeholder="Username" id="email"
                    onChange={(e)=>{setUser(e.target.value)}}
                />
                <input type="email" className="p-1" placeholder="Email" id="password" 
                    onChange={(e)=>{setEmail(e.target.value)}}
                />
                <input type="password" className="p-1" placeholder="Password" id="password" 
                    onChange={(e)=>{setPassword(e.target.value)}}
                />
                <input type="password" className="p-1" placeholder="Confirm Password" id="password" 
                    onChange={(e)=>{setConPassword(e.target.value)}}
                />
                <h4>{err}</h4>
                <button className="btn btn-outline-dark btn-bg" onClick={loginProcess}>Register</button>
                <div className="createAccount">
                    <span>Already have an accout - </span>
                    <a href="">Login</a>
                </div>
            </div>
        );
    }
    else return(<></>);
}

export default Register;
