import { useState,useEffect } from 'react';
import './App.css';
import Nav from './components/navBar/nav';
import Home from './components/Home/home';
import Home2 from './components/Home/home2';
import Create from './components/Create/create';
import Login from './components/Login/login';


function App() {

  const [show, setShow] = useState({
    home : true,
    create : false,
    search : false,
    login : false
  });

  useEffect(()=>{
    const create = document.querySelector(".nav-link:nth-of-type(2)");
    const h = document.querySelector(".nav-link:nth-of-type(1)");
    const l = document.querySelector(".nav-link:nth-of-type(4)");
    create.addEventListener("click", ()=> setShow(
      {
        home : false,
        create : true,
        search : false,
        login : false
      }
    ))
    h.addEventListener("click", ()=> setShow(
      {
        home : true,
        create : false,
        search : false,
        login : false
      }
    ))
    l.addEventListener("click", ()=> {
        setShow(
          {
            home : false,
            create : false,
            search : false,
