import axios from "axios";
import { useEffect, useRef, useState } from "react";

const Register = ({Reg})=>{

    const [register, setReg] = useState(false);
    const [email, setEmail] = useState();
    const [username, setUser] = useState();
    const [password, setPassword] = useState();
    const [conpassword, setConPassword] = useState();
    const [err, setErr] = useState('');
    const loginProcess = ()=> setReg(!register);

    const Password = useRef();

    useEffect(()=>{
        const reg = async ()=>{
            await axios({
                method : "post",
                header : {},
                url : "/register",
                data : {
                    user : `${username}`,
                    e : `${email}`,
                    p : `${password}`
                }
            })
        }
        if(Password.current.innerText == conpassword) reg();
        else setErr('Check Here !!');
    }, [register])

    if(Reg) return(
        <div className="register px-5">
            <input type="text" className="my-2 py-2" placeholder="Username" id="email"
                onChange={(e)=>{setUser(e.target.value)}}
            />
            <input type="email" placeholder="Email" id="password" 
                onChange={(e)=>{setEmail(e.target.value)}}
            />
            <input type="password" placeholder="Password" id="password" 
                onChange={(e)=>{setPassword(e.target.value)}}
                ref={Password}
            />
            <input type="password" placeholder="Confirm Password" id="password" 
                onChange={(e)=>{setConPassword(e.target.value)}}
            />
            <h4>{err}</h4>
            <button className="my-2 btn btn-outline-dark btn-bg" onClick={loginProcess}>Register</button>
            <div className="createAccount">
                <span>Already have an accout - </span>
                <a href="">Login</a>
            </div>
        </div>
    );
    else return(<></>);
}

export default Register;
