import axios from 'axios';
import React, { useEffect, useState, useRef } from 'react';
import './engine.css';
import Create from '../Create/create';


const Engine = (prop)=>{

    const [searchText, setSearch] = useState('');
    const [result, setResult] = useState([]);
    const [edit, setEdit] = useState(
        {
            edit : false,
            title : undefined,
            content : undefined,
            liked : undefined
        }
    );
    const deleteSearch = useRef();
    const resSpan = useRef();

    const DeleteSearch = ()=>{
        setSearch('');
    }

    useEffect(()=>{
        if(searchText.length > 0){
            axios.get(`/searchNote?q=${searchText}`)
            .then((ob)=>{
                console.log(ob.data);
                setResult(ob.data.d);
            })
            .catch((ob)=>{
                console.log(ob);
            })
        }
    },[searchText])

    // const Edit = (id)=>{
    //     const note = result.filter((res)=>{
    //         if(res._id === id) return res;
    //     })
    //     console.log(note);
    //     setTitle(note[0].title);
    //     setContent(note[0].content);
    //     setLiked(note[0].like);
    //     // setEdit(true);
    // }

    if(prop.search){
        return(
            <>
                <div className="engine container-fluid p-3">
                    <input type="text"  className="p-1" placeholder="Search your notes"
                        onChange={(e)=>{
                            setSearch(e.target.value);
                        }}
                        value={searchText}
                    />
                    <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" class="bi bi-x-circle-fill" viewBox="0 0 16 16" ref={deleteSearch} onClick={DeleteSearch}>
                    <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM5.354 4.646a.5.5 0 1 0-.708.708L7.293 8l-2.647 2.646a.5.5 0 0 0 .708.708L8 8.707l2.646 2.647a.5.5 0 0 0 .708-.708L8.707 8l2.647-2.646a.5.5 0 0 0-.708-.708L8 7.293 5.354 4.646z"/>
                    </svg>
                </div>
                <div className="result container-fluid row" >
                    <div className="list col">
                        <ul>
                            {
                                result.map((res)=>(
                                    <li className='row'>
                                        <span className="col">{res.title}</span>
                                        <span className='col text-end'>
                                            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" class='bi bi-pencil-square' viewBox="0 0 16 16" 
                                                onClick={()=>{
                                                    setEdit({
                                                        edit : true,
                                                        title : res.title,
                                                        content : res.content,
                                                        liked : res.like
                                                    });
                                                }}
                                            >
                                            <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                                            <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                                            <span style={{display : "none"}} ref={resSpan}>{res._id}</span>
                                            </svg>
                                        </span>
                                    </li>
                                ))
                            }
                        </ul>
                    </div>
                    <div className="edit col">
                        <Create create={edit.edit} title={edit.title} content={edit.content} like={edit.liked}/>
                    </div>
                </div>
            </>
        );
    }
}

export default Engine;
