import { useState,useEffect, useRef } from 'react';
import './App.css';
import Nav from './components/navBar/nav';
import Home from './components/Home/home';
import Home2 from './components/Home/home2';
import Create from './components/Create/create';
import Login from './components/Login/login';
import Register from './components/Register/register';


function App() {

  const [show, setShow] = useState({
    home : true,
    create : false,
    search : false,
    login : false,
    register : false
  });

  useEffect(()=>{
    const create = document.querySelector(".nav-link:nth-of-type(2)");
    const h = document.querySelector(".nav-link:nth-of-type(1)");
    const l = document.querySelector(".nav-link:nth-of-type(4)");
    const reg = document.querySelector(".createAccount a");
    create.addEventListener("click", ()=> setShow(
      {
        home : false,
        create : true,
        search : false,
        login : false,
        register : false
      }
    ))
    h.addEventListener("click", ()=> setShow(
      {
        home : true,
        create : false,
        search : false,
        login : false,
        register : false
      }
    ))
    l.addEventListener("click", ()=> {
        setShow(
          {
            home : false,
            create : false,
            search : false,
            login : true,
            register : false
          }
        )
    })
    reg.addEventListener("click",()=>{
      setShow(
        {
          home : false,
          create : false,
          search : false,
          login : false,
          register : true
        }
      )
    })
  },[show])

  useEffect(()=>{
      const h = document.querySelector(".nav-link:nth-of-type(1)");
      if(show.home) h.style.borderBottom = "1px solid black";
      else h.style.borderBottom = "1px solid transparent";
  })

  const h = useRef();

  return(
    <>
        <div className="bg"></div>
        <Nav/>
        <div className="home container-fluid p-5 row m-0">
              <h2 className="col p-5 mx-4 my-1 fw-bold fs-2">
                  Your personal notes storage app !!
              </h2>
              <div className="col container-fluid">
                <script>
                  if(show.home){
                    <Home2 home={show.home} h={h}/>
                  }else if(show.create){
                    <Create create={show.create}/>
                  }
                </script>

              </div>
          </div>
      </>
  );


}

export default App;
